import math
from IPython.core.display import display, HTML, Markdown

imagen='''data:image/png;base64,'''


def grados_a_radianes(grados):
    display(HTML("<img style='width:300px' src="+imagen+"><span style='color:purple'><b>¡Recuerda!</b> La conversión de grados a radianes se realiza con la siguiente fórmula:</span><BR><H3>$radianes=\\frac{grados*\pi}{180}$</H3>"))
    return(grados*math.pi/180)
    
def radianes_a_grados(radianes):
    display(HTML("<img style='width:300px' src="+imagen+"><span style='color:purple'><b>¡Recuerda!</b> La conversión de radianes a grados se realiza con la siguiente fórmula:</span><BR><H3>$grados=\\frac{radianes*\pi}{180}$</H3>"))
    return(radianes*180/math.pi)